mapper.select:
     select * from production 
	 where pname = (select pname from procontain 
	 				where pbno=#{pbno}
	 				)

select count(*) from (
	select rownum rn, pb.*
	from (
	    select pb.*, pname, category1, category2, category3 
	    from proboard pb, (
	        select DISTINCT p.pname, p.category1, p.category2, p.category3, c.pbno
	        from production p, procontain c
	        where p.pno = c.pno


				     and category2 in

				       #{item}



				  	 and category3 in

					    #{item}



	    ) c
	    where pb.pbno = c.pbno and pb.title like '%'||#{kwd}||'%'
	    order by pb.pbno desc
	) pb
	order by rn
)

	select rownum rn, s.*
	from (
        	select DISTINCT b.*, p.category1, p.category2, p.category3 
			from (
				select pb.*, pname
				from (
	                select distinct * from proboard
	            ) pb 
	            left outer join procontain b
	            on pb.pbno = b.pbno
	            where pb.title like '%'||#{kwd}||'%'           
			) b
			left outer join production p
			on p.pname = b.pname
	    ) s
	order by rn

select * from (
	select b.*
	from(
		select rownum rn, pb.*
		from proboard pb
		where title like '%'||#{kwd}||'%'			
		order by pbno desc
	) b
	order by rn
) where rn between #{startNum} and #{endNum}

select * from (
	select rownum rn, pb.*
	from (
	    select pb.*, pname, category1, category2, category3 
	    from proboard pb, (
	        select DISTINCT p.pname, p.category1, p.category2, p.category3, c.pbno
	        from production p left outer join procontain c
	        on (p.pno = c.pno)
	        where 1 = 1


				     and category2 in

				       #{item}



				  	 and category3 in

					    #{item}



	    ) c
	    where pb.pbno = c.pbno and pb.title like '%'||#{kwd}||'%'
	    order by pb.pbno desc
	) pb
	order by rn
) where rn between #{startNum} and #{endNum}
